mapper.select:
select r.id id, t.tech_name item_name,t.tech_price item_price,'检验' item_type,t.tech_format item_format,r.creation_time item_create_time from disposal_request r, medical_technology t where r.medical_technology_id = t.id
and r.check_state = '已开立'
and r.register_id = #{register_id}

select count(id)
from register r,(select register_id,disposal_state from disposal_request group by register_id, disposal_state having disposal_state = '已缴费') c
where r.id = c.register_id

    and r.real_name = #{real_name}


    and r.case_number = #{case_number}

and visit_state = 3

select count(id)
from register r,(select register_id,disposal_state from disposal_request group by register_id, disposal_state having disposal_state = '已缴费') c
where r.id = c.register_id

    and r.real_name = #{real_name}


    and r.case_number = #{case_number}

select r.*
from register r ,(select register_id,disposal_state from disposal_request group by register_id, disposal_state having disposal_state = '已缴费') c
where r.id = c.register_id

    and r.case_number = #{case_number}


    and r.real_name = #{real_name}

ORDER BY r.visit_date
LIMIT ${(nowPageNumber-1) * pageSize} ,#{pageSize}
